CREATE OR REPLACE FUNCTION get_clipboard_field_desc (ie_field_p text, ie_record_type_p text ) RETURNS varchar AS $body$
DECLARE

				
ds_field_desc_w varchar(255);



BEGIN

if (ie_record_type_p IS NOT NULL AND ie_record_type_p::text <> '' AND ie_field_p IS NOT NULL AND ie_field_p::text <> '') then

    select max(z.DS_EXPRESSAO )
    into STRICT ds_field_desc_w 
    from (
