    select  DS_EXPRESSAO   from valor_dominio_v where cd_dominio = 10056
    AND ie_record_type_p = 'SUR_HIST' and vl_dominio = ie_field_p
     
union

    select  DS_EXPRESSAO   from valor_dominio_v where cd_dominio = 10056
    AND ie_record_type_p = 'TRANS_HIST' and vl_dominio = ie_field_p
    
