    select  DS_EXPRESSAO   from valor_dominio_v where  cd_dominio = 10062
    AND ie_record_type_p = 'SNCP' and vl_dominio = ie_field_p
    
union

    select  DS_EXPRESSAO   from valor_dominio_v where cd_dominio = 10055
    AND ie_record_type_p = 'FAM_HIST' and vl_dominio = ie_field_p
    
