    select  DS_EXPRESSAO   from valor_dominio_v where cd_dominio = 10057
    AND ie_record_type_p = 'CLINIC_STAFF' and vl_dominio = ie_field_p
    
union
 
    select  DS_EXPRESSAO   from valor_dominio_v where cd_dominio = 10064
