CREATE OR REPLACE FUNCTION get_component_pos_dis_func (nm_usuario_p conf_splitter_dis.nm_usuario%type, cd_tab_p conf_splitter_dis.cd_tab%type, nm_componente_p conf_splitter_dis.nm_componente%type) RETURNS bigint AS $body$
DECLARE


ds_retorno_w bigint := 0;


BEGIN

    if (nm_componente_p IS NOT NULL AND nm_componente_p::text <> '') then
        select nr_posicao
          into STRICT ds_retorno_w
          from conf_splitter_dis
         where nm_componente = nm_componente_p
		   and cd_tab = cd_tab_p
		   and nm_usuario = nm_usuario_p;
    else
        select nr_posicao
          into STRICT ds_retorno_w
          from conf_splitter_dis
         where nm_usuario = nm_usuario_p
		   and cd_tab = cd_tab_p  LIMIT 1;
    end if;

return ds_retorno_w;

end;
