CREATE OR REPLACE FUNCTION get_concatenated_date ( dt_referencia_p timestamp, hr_hora_ref_p text, ie_tipo_item_p text, nr_horas_copia_p bigint, dt_inicio_copia_p timestamp, cd_intervalo_p text) RETURNS timestamp AS $body$
DECLARE

	dt_referencia_w		timestamp := dt_referencia_p;
	dt_horario_w		timestamp;
	hr_hora_ref_w		varchar(20) := hr_hora_ref_p;
	nr_count_w			smallint;

BEGIN

if (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') and (hr_hora_ref_w IS NOT NULL AND hr_hora_ref_w::text <> '') and (hr_hora_ref_w <> '  :  ') and (hr_hora_ref_w <> '__:__') then
	
	dt_horario_w := null;
	dt_referencia_w := to_date(to_char(dt_referencia_p,'dd/mm/yyyy')||hr_hora_ref_w,'dd/mm/yyyy hh24:mi');	
	
	if (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then
		select	count(*)
		  into STRICT  nr_count_w
		  from	intervalo_prescricao
		 where	cd_intervalo = cd_intervalo_p
		   and 	ie_operacao = 'H'
		   and  qt_operacao > 12;
		
		if (nr_count_w > 0) then
			hr_hora_ref_w := to_char(cpoe_obter_inicio_interv_horas(cd_intervalo_p, dt_referencia_w, dt_referencia_w),'hh24:mi');
			dt_referencia_w := to_date(to_char(dt_referencia_p,'dd/mm/yyyy')||hr_hora_ref_w,'dd/mm/yyyy hh24:mi');
		end if;
	end if;		
	
	if (trunc(clock_timestamp()) <> trunc(dt_referencia_p)) and (ie_tipo_item_p <> 'P') then
		dt_horario_w 	:= to_date(to_char(clock_timestamp(),'dd/mm/yyyy')||hr_hora_ref_w,'dd/mm/yyyy hh24:mi');
		if (dt_horario_w between(dt_referencia_p - nr_horas_copia_p/24) and dt_referencia_p)  and (dt_horario_w > trunc(coalesce(dt_inicio_copia_p,clock_timestamp()) +1/24,'hh24')) then
			dt_referencia_w := dt_horario_w;
		end if;	
	end if;
	
	if (dt_referencia_w < dt_referencia_p) and
	   ((dt_referencia_w < (dt_referencia_p -nr_horas_copia_p/24)) or (ie_tipo_item_p = 'P'))	then
			
		dt_referencia_w := dt_referencia_w + 1;
	
	end if;
end if;

return dt_referencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
