CREATE OR REPLACE FUNCTION get_cpoe_proc_approved (nr_seq_lista_espera_p agenda_lista_espera.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_aprov_esp_cpoe_w				proc_interno.ie_aprov_esp_cpoe%type;
dt_special_approval_w			cpoe_procedimento.dt_special_approval%type;
ie_proc_approved				varchar(10) := null;


BEGIN

select	max(a.ie_aprov_esp_cpoe),
		max(b.dt_special_approval)
into STRICT	ie_aprov_esp_cpoe_w,
		dt_special_approval_w
from	proc_interno a,
		cpoe_procedimento b
where	b.nr_seq_lista_espera = nr_seq_lista_espera_p
and		b.nr_seq_proc_interno = a.nr_sequencia;

if (ie_aprov_esp_cpoe_w = 'RAD' and coalesce(dt_special_approval_w::text, '') = '') then

	ie_proc_approved := 'PA';
	
else

	ie_proc_approved := 'A';
	
end if;

return ie_proc_approved;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
