SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_cpoe_rp_description (nr_sequencia_p bigint, ie_type_out_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_type_out_p:
F = full
S = short
*/
ds_return_w varchar(2000);
ds_rp_w varchar(255);
ds_interval_w varchar(255);
ds_route_w varchar(255);
ds_start_date_w varchar(255);
ds_end_date_w varchar(255);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '')then

	select  nr_Sequencia_rp,
	  obter_desc_intervalo(cd_intervalo),
	  obter_ds_ie_via_aplicacao(ie_via_aplicacao),
	  pkg_date_formaters.to_varchar(dt_inicio,'short',wheb_usuario_pck.get_cd_estabelecimento,wheb_usuario_pck.get_nm_usuario),
	   pkg_date_formaters.to_varchar(dt_fim,'short',wheb_usuario_pck.get_cd_estabelecimento,wheb_usuario_pck.get_nm_usuario)
	into STRICT
	  ds_rp_w,
	  ds_interval_w,
	  ds_route_w,
	  ds_start_date_w,
	  ds_end_date_w
	from cpoe_rp
	where nr_Sequencia = nr_Sequencia_p;


	if (ie_type_out_p = 'F') then
	  ds_return_w := 'RP' || ds_rp_w || ' - ' || ds_interval_w || ' - ' || ds_route_w || ' - ' || ds_start_date_w || ' - ' || ds_end_date_w;
	elsif (ie_type_out_p = 'S') then
	  ds_return_w := 'RP' || ds_rp_w;
	end if;
	
end if;

return ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
