SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_custom_branch_html5 (nr_service_order_p bigint) RETURNS varchar AS $body$
DECLARE
		
    cd_tasy_version_w varchar(15);
    qt_branch_w		bigint;
    cd_cnpj_w		varchar(25);
    branch_name_w varchar(100) := '';

BEGIN
    if (nr_service_order_p > 0) then
        select	coalesce(max(m.cd_tasy_version),'0') into STRICT cd_tasy_version_w
        from	man_ordem_servico m
        where	m.nr_sequencia = nr_service_order_p;

        select	coalesce(max(c.cd_cnpj),'0') into STRICT cd_cnpj_w
        from	man_ordem_servico m,
                com_cliente c
        where	c.nr_sequencia = m.nr_seq_cliente
        and		m.nr_sequencia = nr_service_order_p;

        BEGIN
          select distinct a.cd_version || '.' || a.ds_hotfix into STRICT branch_name_w 
          from service_pack_hotfix a,
               service_order_sp_hotfix b
          where	a.nr_sequencia = b.nr_service_pack_hotfix
            and a.cd_version = cd_tasy_version_w
            and b.cd_cnpj = cd_cnpj_w  LIMIT 1;
        EXCEPTION
         when no_data_found THEN
            branch_name_w := '';
        END;
    end if;
    return branch_name_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
