CREATE OR REPLACE FUNCTION get_custom_branch (nr_service_order_p bigint) RETURNS varchar AS $body$
DECLARE
		
    ie_plataforma_w varchar(15);

BEGIN
	select	ie_plataforma
	into STRICT	ie_plataforma_w
	from	man_ordem_servico
	where	nr_sequencia = nr_service_order_p;


    if (ie_plataforma_w = 'H') then
        return get_custom_branch_html5(nr_service_order_p);
    elsif (ie_plataforma_w = 'J') then
        return customer_branch_exists(nr_service_order_p);
    else
	return '';
    end if;
    EXCEPTION
      WHEN OTHERS THEN
      return '';
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
