SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_custom_risk_exists ( ie_escala_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
nr_seq_conduta_esc_w		pep_conduta.nr_seq_conduta_esc%type;
record_exists_w			bigint;
nr_seq_inf_w			informacao_suep.nr_seq_inf%type;
ie_escala_w			pep_informacao.ie_escala%type;


BEGIN

	begin
	select	nr_seq_inf
	into STRICT	nr_seq_inf_w
	from	informacao_suep
	where	nr_sequencia = ie_escala_p;
	exception
	when others then
		nr_seq_inf_w := null;
	end;
	
	if (nr_seq_inf_w IS NOT NULL AND nr_seq_inf_w::text <> '') then
		begin
		select	ie_escala
		into STRICT	ie_escala_w
		from	pep_informacao
		where	nr_sequencia = nr_seq_inf_w;
		exception
		when others then
			ie_escala_w := null;
		end;	
	end if;
	
		
	if (ie_escala_w IS NOT NULL AND ie_escala_w::text <> '') then
		begin
		select	max(nr_sequencia)
		into STRICT	nr_seq_conduta_esc_w
		from	pep_conduta_escala
		where	ie_escala = ie_escala_w;
		exception
		when others then
			nr_seq_conduta_esc_w := null;
		end;
	end if;
	
	if (nr_seq_conduta_esc_w IS NOT NULL AND nr_seq_conduta_esc_w::text <> '') then
		select	count(*)
		into STRICT	record_exists_w
		from	pep_conduta
		where	nr_seq_conduta_esc	=	nr_seq_conduta_esc_w
		and	ie_range_risco		=	'S';
	end if;
	
	if (record_exists_w > 0 ) then
		ds_retorno_w := 'EXISTS';
	else
		ds_retorno_w := 'NOTEXISTS';
	end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
