SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_custom_risk_ranges ( current_value_p bigint, ie_escala_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
nr_seq_conduta_esc_w		pep_conduta.nr_seq_conduta_esc%type;
record_exists_w			bigint;
qt_ponto_min_w			pep_conduta.qt_ponto_min%type;
qt_ponto_max_w			pep_conduta.qt_ponto_max%type;
nr_seq_inf_w			informacao_suep.nr_seq_inf%type;
ie_escala_w			pep_informacao.ie_escala%type;
nr_seq_pep_conduta_w		pep_conduta.nr_sequencia%type;
flex_record_w			informacao_suep.nr_seq_score_flex%type;
flex_ii_record_w		informacao_suep.nr_seq_score_flex_ii%type;


BEGIN

	select	nr_seq_inf,
		nr_seq_score_flex,
		nr_seq_score_flex_ii
	into STRICT	nr_seq_inf_w,
		flex_record_w,
		flex_ii_record_w
	from	informacao_suep
	where	nr_sequencia = ie_escala_p;
	
	select	ie_escala
	into STRICT	ie_escala_w
	from	pep_informacao
	where	nr_sequencia = nr_seq_inf_w;
	
	if (flex_record_w IS NOT NULL AND flex_record_w::text <> '') then
		select	nr_sequencia
		into STRICT	nr_seq_conduta_esc_w
		from	pep_conduta_escala
		where	ie_escala = ie_escala_w
		and	nr_seq_score_flex = flex_record_w
		order by nr_sequencia desc LIMIT 1;
	end if;
	
	if (flex_ii_record_w IS NOT NULL AND flex_ii_record_w::text <> '') then
		select	nr_sequencia
		into STRICT	nr_seq_conduta_esc_w
		from	pep_conduta_escala
		where	ie_escala = ie_escala_w
		and	nr_seq_score_flex_ii = flex_ii_record_w
		order by nr_sequencia desc LIMIT 1;
	end if;
	
	if (coalesce(flex_record_w::text, '') = '' and coalesce(flex_ii_record_w::text, '') = '') then
		select	nr_sequencia
		into STRICT	nr_seq_conduta_esc_w
		from	pep_conduta_escala
		where	ie_escala = ie_escala_w
		order by nr_sequencia desc LIMIT 1;
	end if;
	
	select	nr_sequencia,
		qt_ponto_min,
		qt_ponto_max
	into STRICT	nr_seq_pep_conduta_w,
		qt_ponto_min_w,
		qt_ponto_max_w
	from	pep_conduta
	where	nr_seq_conduta_esc 	=	nr_seq_conduta_esc_w
	and	ie_range_risco		=	'S'
	order	by nr_sequencia desc LIMIT 1;
	
	if (current_value_p >= qt_ponto_min_w and current_value_p <= qt_ponto_max_w) then
		ds_retorno_w := 'RISK';
	else
		ds_retorno_w := 'NORMAL';
	end if;
	

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
