SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_cycle_week_description (selected_date_p timestamp, cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


	week_description_w	varchar(255);
	cycle_start_date_w timestamp;
	cycle_end_date_w timestamp;
	nr_seq_w bigint;
	week_w bigint;
	total_weeks_count bigint;
	repeated_week_w bigint;


BEGIN

	select max(nr_sequencia),
		max(dt_validity_start),
		max(dt_validity_end)
	into STRICT nr_seq_w,
		cycle_start_date_w,
		cycle_end_date_w
	from	schedule_cycle 
	where	nr_seq_agenda = cd_agenda_p
	and	selected_date_p between dt_validity_start and coalesce(dt_validity_end, pkg_date_utils.end_of(selected_date_p, 'DAY'));

	if (nr_seq_w IS NOT NULL AND nr_seq_w::text <> '')	then

	  select count(*)
	  into STRICT	total_weeks_count
	  from	schedule_cycle_week 
	  where	nr_seq_schedule_cycle = nr_seq_w;
	
		if (total_weeks_count > 0) then

		  select ceil(abs((to_date(cycle_start_date_w) - 1) - to_date(selected_date_p))/7)
		  into STRICT week_w 
		;
	
		end if;
		
	  if ( week_w <= total_weeks_count ) then

		select ds_week
		into STRICT week_description_w
		from schedule_cycle_week
		where nr_seq_schedule_cycle = nr_seq_w
		and nr_presentation_order = week_w;

	  elsif (week_w > total_weeks_count) then

		select mod(week_w, total_weeks_count)
		into STRICT repeated_week_w
		;
	
	  if (repeated_week_w = 0) then
  
		repeated_week_w := total_weeks_count;

	  end if;
	
		select ds_week
		into STRICT week_description_w
		from schedule_cycle_week
		where nr_seq_schedule_cycle = nr_seq_w
		and nr_presentation_order = repeated_week_w;
	  
	  end if;

	end if;

return week_description_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
