SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_data_doc_treatment_plan ( nr_atendimento_p bigint, si_option_return_p text, si_type_form_p text) RETURNS varchar AS $body$
DECLARE


/*si_option_return_p
SEQ	 NR_SEQUENCIA
CDD	CD_DEPARTMENT
DSD	DS_DEPARTMENT
CDW	CD_WARD
DSW	DS_WARD
CDAD	CD_ATTENDING_DOCTOR
NMAD	NM_ATTENDING_DOCTOR
IEST	IE_STATUS
DSST	DS_STATUS
SITF	SI_TYPE_FORM
SINU	SI_SPECIAL_NUTRITION_CONTROL */
nr_seq_document_w		med_treatment_plan.nr_sequencia%type;
cd_department_w			med_treatment_plan.cd_department%type;
ds_department_w			departamento_medico.ds_departamento%type;
cd_ward_w			med_treatment_plan.cd_ward%type;
ds_ward_w			setor_atendimento.ds_setor_atendimento%type;
cd_physician_w			pessoa_fisica.cd_pessoa_fisica%type;
nm_physician_w			pessoa_fisica.nm_pessoa_fisica%type;
ie_status_w			valor_dominio.vl_dominio%type;
ds_status_w			dic_expressao.ds_expressao_br%type;
si_type_form_w			med_treatment_plan.si_type_form%type;
si_special_nutrition_control_w	med_treatment_plan.si_special_nutrition_control%type;
ds_return_w			varchar(255);
			

BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_document_w
from	med_treatment_plan
where	nr_atendimento = nr_atendimento_p
and	ie_situacao = 'A'
and	((si_type_form_p = '0') or /*All forms*/
	 (si_type_form_p = si_type_form) or
	 ((si_type_form_p = '1234') and (si_type_form in ('1','2','3','4'))));

/*SEQ	 NR_SEQUENCIA*/

if (si_option_return_p = 'SEQ') and (nr_seq_document_w > 0) then
	begin
	ds_return_w := nr_seq_document_w;
	end;
	
/*CDD	CD_DEPARTMENT*/

elsif (si_option_return_p = 'CDD') and (nr_seq_document_w > 0) then
	begin
	select	cd_department
	into STRICT	cd_department_w
	from	med_treatment_plan
	where	nr_sequencia = nr_seq_document_w;	
	
	ds_return_w := cd_department_w;
	end;
	
/*DSD	DS_DEPARTMENT*/

elsif (si_option_return_p = 'DSD') and (nr_seq_document_w > 0) then
	begin
	select	substr(obter_nome_departamento_medico(cd_department),1,255)
	into STRICT	ds_department_w
	from	med_treatment_plan
	where	nr_sequencia = nr_seq_document_w;	
	
	ds_return_w := ds_department_w;
	end;
	
/*CDW	CD_WARD*/

elsif (si_option_return_p = 'CDW') and (nr_seq_document_w > 0) then
	begin
	select	cd_ward
	into STRICT	cd_ward_w
	from	med_treatment_plan
	where	nr_sequencia = nr_seq_document_w;	
	
	ds_return_w := cd_ward_w;
	end;

/*DSW	DS_WARD*/

elsif (si_option_return_p = 'DSW') and (nr_seq_document_w > 0) then
	begin	
	select	substr(obter_nome_setor(cd_ward),1,255)
	into STRICT	ds_ward_w
	from	med_treatment_plan
	where	nr_sequencia = nr_seq_document_w;	
	
	ds_return_w := ds_ward_w;
	end;

/*CDAD	CD_ATTENDING_DOCTOR*/

elsif (si_option_return_p = 'CDAD') and (nr_seq_document_w > 0) then
	begin	
	select	max(b.cd_physician)
	into STRICT	cd_physician_w
	from	med_treatment_plan a,
		med_treatment_plan_team b
	where	a.nr_sequencia = b.nr_seq_med_treat_plan
	and	a.nr_sequencia = nr_seq_document_w
	and	si_person_type = '1';
	
	ds_return_w := cd_physician_w;
	end;
	
/*NMAD	NM_ATTENDING_DOCTOR*/

elsif (si_option_return_p = 'NMAD') and (nr_seq_document_w > 0) then
	begin
	
	select	substr(max(obter_nome_medico(b.cd_physician,'N')),1,255)
	into STRICT	nm_physician_w
	from	med_treatment_plan a,
		med_treatment_plan_team b
	where	a.nr_sequencia = b.nr_seq_med_treat_plan
	and	a.nr_sequencia = nr_seq_document_w
	and	si_person_type = '1';
	
	ds_return_w := nm_physician_w;
	end;
	
/*SIFT	SI_TYPE_FORM*/

elsif (si_option_return_p = 'SITF') and (nr_seq_document_w > 0) then
	begin

	select	si_type_form
	into STRICT	si_type_form_w
	from	med_treatment_plan
	where	nr_sequencia = nr_seq_document_w;

	ds_return_w := si_type_form_w;
	end;
	
/*IEST	IE_STATUS
DSST	DS_STATUS*/
elsif (si_option_return_p in ('IEST','DSST')) then
	begin	
	
	if (coalesce(nr_seq_document_w,0) = 0) then
		ie_status_w := 'N'; /*Not created*/
		ds_status_w := wheb_mensagem_pck.get_Texto(1147367); /*Not created*/
	else
		select	CASE WHEN coalesce(dt_liberacao::text, '') = '' THEN 'C'  ELSE 'I' END  /*Created or Issued*/
,
			CASE WHEN coalesce(dt_liberacao::text, '') = '' THEN wheb_mensagem_pck.get_Texto(1147368)  ELSE wheb_mensagem_pck.get_Texto(1147369) END  /*Created or Issued*/
		into STRICT	ie_status_w,
			ds_status_w
		from	med_treatment_plan
		where	nr_sequencia = nr_seq_document_w;				
	end if;	
	
	if (si_option_return_p = 'IEST') then
		ds_return_w := ie_status_w;
	elsif (si_option_return_p = 'DSST') then
		ds_return_w := ds_status_w;
	end if;	
	end;	

/*SINU	SI_SPECIAL_NUTRITION_CONTROL */

elsif (si_option_return_p = 'SINU') and (nr_seq_document_w > 0) then
	begin

	select	si_special_nutrition_control
	into STRICT	si_special_nutrition_control_w
	from	med_treatment_plan
	where	nr_sequencia = nr_seq_document_w;

	ds_return_w := si_special_nutrition_control_w;
	end;	
end if;

return	ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION get_data_doc_treatment_plan ( nr_atendimento_p bigint, si_option_return_p text, si_type_form_p text) FROM PUBLIC;

