CREATE OR REPLACE FUNCTION get_data_medical_department ( cd_departamento_p bigint, ie_option_p text) RETURNS varchar AS $body$
DECLARE


  

/* 

DS - DS_DEPARTAMENTO 

ES - CD_EXTERNAL_SYSTEM */
 

  

ds_departamento_w        departamento_medico.ds_departamento%type;

cd_external_system_w        departamento_medico.cd_departamento%type;

ds_return_w            departamento_medico.ds_departamento%type;




BEGIN 

  

if (cd_departamento_p > 0) then 

  

    select    ds_departamento, 

        cd_departamento 

    into STRICT    ds_departamento_w, 

        cd_external_system_w 

    from    departamento_medico 

    where    cd_departamento = cd_departamento_p;

     

    if (ie_option_p = 'DS') then 

        ds_return_w    := ds_departamento_w;

    elsif (ie_option_p = 'DS') then 

        ds_return_w    := cd_external_system_w;

    end if;

end if;

  

return    ds_return_w;

  

end;
$body$
