CREATE OR REPLACE FUNCTION get_data_type_additional ( cd_pessoa_fisica_p compl_pessoa_fisica.cd_pessoa_fisica%type, ie_tipo_complemento_p compl_pessoa_fisica.ie_tipo_complemento%type, ie_info_p text) RETURNS varchar AS $body$
DECLARE


/*
	A = Adress,
	P = Phone number
*/
ds_return_w	varchar(255);


BEGIN

	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '' AND ie_tipo_complemento_p IS NOT NULL AND ie_tipo_complemento_p::text <> '') then
	
		if (ie_info_p = 'A') then
		
			select  substr(max(cpf.ds_endereco)||', '||max(cpf.ds_compl_end), 1, 255)
			into STRICT	ds_return_w
			from    compl_pessoa_fisica cpf
			where   cpf.cd_pessoa_fisica 	= cd_pessoa_fisica_p
			and     cpf.ie_tipo_complemento = ie_tipo_complemento_p;
			
		elsif (ie_info_p = 'P') then
		
			select  substr(coalesce(max(ds_fone_adic), max(nr_telefone)), 1, 255)
			into STRICT	ds_return_w
			from    compl_pessoa_fisica cpf
			where   cpf.cd_pessoa_fisica 	= cd_pessoa_fisica_p
			and     cpf.ie_tipo_complemento = ie_tipo_complemento_p;
			
		end if;
		
	end if;

return	ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
