CREATE OR REPLACE FUNCTION get_dates_external_refferal ( nr_sequencia_p bigint, si_type_record_p bigint) RETURNS timestamp AS $body$
DECLARE


/*si_type_record_p
1 - RL_REF_FROM_HOSPITAL table
2 - RL_REP_TO_HOSPITAL table
3 - RL_REF_TO_HOSPITAL table
4 - RL_REP_FROM_HOSPITAL table*/
			
			
dt_record_w			rl_ref_from_hospital.dt_reference%type;
			

BEGIN

if (si_type_record_p = 1) then
	select	coalesce(dt_reference,dt_atualizacao_nrec)
	into STRICT	dt_record_w
	from	rl_ref_from_hospital
	where	nr_sequencia = nr_sequencia_p;	
elsif (si_type_record_p = 2) then
	select	coalesce(dt_reference,dt_atualizacao_nrec)
	into STRICT	dt_record_w
	from	rl_rep_to_hospital
	where	nr_sequencia = nr_sequencia_p;
elsif (si_type_record_p = 3) then
	select	coalesce(dt_reference,dt_atualizacao_nrec)
	into STRICT	dt_record_w
	from	rl_ref_to_hospital
	where	nr_sequencia = nr_sequencia_p;	
elsif (si_type_record_p = 4) then
	select	coalesce(dt_reception,dt_atualizacao_nrec)
	into STRICT	dt_record_w
	from	rl_rep_from_hospital
	where	nr_sequencia = nr_sequencia_p;
end if;

return	dt_record_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
