CREATE OR REPLACE FUNCTION get_days_by_classification (nr_atendimento_p bigint, cd_classification_p bigint) RETURNS bigint AS $body$
DECLARE

/*
Get patient total days stayed in the deparment by classification

4 - ICU

*/
			
dt_entry_w			timestamp;
dt_exit_w			timestamp;
qt_days_icu_w			bigint := 0;	
ie_has_values_w			smallint;		
			
			
C01 CURSOR FOR
SELECT	trunc(dt_entrada_unidade),
	trunc(coalesce(dt_saida_unidade, clock_timestamp()))
from	atend_paciente_unidade
where	nr_atendimento = nr_atendimento_p
and	obter_classif_setor(cd_setor_atendimento) = cd_classification_p
order by 1;
			
			

BEGIN

open C01;
loop
fetch C01 into	
	dt_entry_w,
	dt_exit_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	qt_days_icu_w := qt_days_icu_w + Obter_Dias_Entre_Datas(dt_entry_w,dt_exit_w);
	
	ie_has_values_w	:= 1;
	end;
end loop;
close C01;

if	(ie_has_values_w = 1 AND qt_days_icu_w = 0) then
	qt_days_icu_w := 1;
end if;

return qt_days_icu_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
