CREATE OR REPLACE FUNCTION get_dept_transfer_japan_date ( nr_atendimento_p transf_departamento_medico.nr_atendimento%type, dt_referencia_p transf_departamento_medico.dt_transferencia%type default clock_timestamp()) RETURNS timestamp AS $body$
DECLARE


cd_return_data_w transf_departamento_medico.dt_transferencia%type;
/*
TRANSFER_DATE
*/
BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select max(a.dt_transferencia)
	into STRICT	cd_return_data_w
	from	transf_departamento_medico a, 
		departamento_medico b
	where	a.cd_depart_destino 	= b.cd_departamento
	and	a.nr_atendimento 	= nr_atendimento_p
	and	a.dt_transferencia 	= (	SELECT	max(x.dt_transferencia)
						from	transf_departamento_medico x
						where	x.nr_atendimento = nr_atendimento_p
						and	x.dt_transferencia <= dt_referencia_p);
end if;

return	cd_return_data_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
