CREATE OR REPLACE FUNCTION get_dept_transfer_japan_number ( nr_atendimento_p transf_departamento_medico.nr_atendimento%type, ie_option_p transf_departamento_medico.nm_usuario%type, dt_referencia_p transf_departamento_medico.dt_transferencia%type default clock_timestamp()) RETURNS bigint AS $body$
DECLARE


cd_dept_dest_w	transf_departamento_medico.cd_depart_destino%type;
cd_motivo_transf_w	transf_departamento_medico.cd_motivo_transf%type;
cd_return_data_w transf_departamento_medico.cd_depart_destino%type;
ie_option_med_dest_w transf_departamento_medico.nm_usuario%type := 'DEST_DEPT';
ie_option_reason_trnsfr_w transf_departamento_medico.nm_usuario%type := 'REASON_TRANSFER';
/*
REASON_TRANSFER
DEST_DEPT
*/
BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '' AND ie_option_p IS NOT NULL AND ie_option_p::text <> '') then

	select	max(b.cd_departamento),max(a.cd_motivo_transf)
	into STRICT	cd_dept_dest_w,cd_motivo_transf_w
	from	transf_departamento_medico a, 
		departamento_medico b
	where	a.cd_depart_destino 	= b.cd_departamento
	and	a.nr_atendimento 	= nr_atendimento_p
	and	a.dt_transferencia 	= (	SELECT	max(x.dt_transferencia)
						from	transf_departamento_medico x
						where	x.nr_atendimento = nr_atendimento_p
						and	x.dt_transferencia <= dt_referencia_p);

    if (ie_option_p = ie_option_med_dest_w) then
    cd_return_data_w := cd_dept_dest_w;
    elsif (ie_option_p = ie_option_reason_trnsfr_w) then
    cd_return_data_w := cd_motivo_transf_w;
    end if;

end if;

return	cd_return_data_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
