CREATE OR REPLACE FUNCTION get_descritpion_score_flex ( cd_setor_atendimento_p bigint, ie_campo_p text default '1') RETURNS varchar AS $body$
DECLARE


ds_descricao_w			varchar(255);
ie_complexidade_nut_w		varchar(10);
ie_complexidade_nut2_w		varchar(10);
nr_seq_score_flex_W			bigint;


BEGIN

	if (ie_campo_p = '1') then
		select	max(ie_complexidade_nut),
				max(nr_seq_score_flex)
		into STRICT  	ie_complexidade_nut_w,
				nr_seq_score_flex_W
		from 	setor_atendimento
		where 	cd_setor_atendimento = cd_setor_atendimento_p;

		if (ie_complexidade_nut_w = 'SF') then

			select	substr(max(ds_escala),1,255)
			into STRICT 	ds_descricao_w
			from	eif_escala
			where 	nr_sequencia = nr_seq_score_flex_w;

		elsif (ie_complexidade_nut_w = 'SFII') then

			select	substr(max(ds_escala),1,255)
			into STRICT 	ds_descricao_w
			from	eif_escala_ii
			where 	nr_sequencia  = nr_seq_score_flex_w;

		end if;
	elsif (ie_campo_p = '2') then

		select	max(ie_complexidade_nut2),
				max(nr_seq_score_flex_ii)
		into STRICT  	ie_complexidade_nut2_w,
				nr_seq_score_flex_W
		from 	setor_atendimento
		where 	cd_setor_atendimento = cd_setor_atendimento_p;

		if (ie_complexidade_nut2_w = 'SF') then

			select	substr(max(ds_escala),1,255)
			into STRICT 	ds_descricao_w
			from	eif_escala
			where 	nr_sequencia = nr_seq_score_flex_W;

		elsif (ie_complexidade_nut2_w = 'SFII') then

			select	substr(max(ds_escala),1,255)
			into STRICT 	ds_descricao_w
			from	eif_escala_ii
			where 	nr_sequencia = nr_seq_score_flex_W;
		end if;
    end if;

return	ds_descricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
