CREATE OR REPLACE FUNCTION get_desc_material_gen_jp ( cd_material_p MATERIAL.CD_MATERIAL%type, ie_validate_locale_p text default 'N') RETURNS varchar AS $body$
DECLARE


ds_retorno_w    varchar(400);


BEGIN
    SELECT CASE WHEN GET_IE_GENERAL_NAME_JP(cd_material_p, null, ie_validate_locale_p) = 'S'
        THEN ds_material ||' (' ||ds_general_name|| ')' 
        ELSE ds_material END
    INTO STRICT ds_retorno_w
    FROM material
    WHERE CD_MATERIAL = cd_material_p;

    RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
