CREATE OR REPLACE FUNCTION get_desc_vinculo ( nr_vinculo_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(150);


BEGIN
        if (ie_opcao_p = 'T') then
                begin
                select  substr((cd_tipo_vinculo||'-'||ds_tipo_vinculo),1,150)
                into STRICT    ie_retorno_w
                from    cnes_tipo_vinculo
                where   nr_sequencia = nr_vinculo_p;
                exception
                        when others then
                        ie_retorno_w := null;
                end;
        elsif (ie_opcao_p = 'S') then
                begin
                select  substr((cd_subtipo_vinculo||'-'||ds_subtipo_vinculo),1,150)
                into STRICT    ie_retorno_w
                from    cnes_sub_tipo_vinculo
                where   cd_subtipo_vinculo = lpad(nr_vinculo_p,'6','0');
                exception
                        when others then
                        ie_retorno_w := null;
                end;
        end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
