CREATE OR REPLACE FUNCTION get_desired_location ( nr_sequencia_p bigint ) RETURNS varchar AS $body$
DECLARE


nr_seq_cpoe_procedimento_w cpoe_procedimento.nr_sequencia%TYPE;
ds_setor_atendimento_w     varchar(100);


BEGIN

    select  max(a.nr_seq_cpoe_procedimento)
    into STRICT    nr_seq_cpoe_procedimento_w
    from    rp_tratamento a
    where   a.nr_sequencia = nr_sequencia_p;

    SELECT  obter_desc_setor_atend(max(cd_setor_atendimento))
    into STRICT    ds_setor_atendimento_w
    FROM    cpoe_procedimento
    WHERE   nr_sequencia = nr_seq_cpoe_procedimento_w;

    return ds_setor_atendimento_w;

end;
