CREATE OR REPLACE FUNCTION get_dosage_form_material ( cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);


BEGIN

	select	coalesce(max(a.ds_forma_farmaceutica),'')
	into STRICT	ds_retorno_w
	from	anvisa_forma_formaceutica a,
			material b
	where	a.ie_situacao = 'A'
	and		a.nr_sequencia = b.nr_seq_forma_farm
	and		b.cd_material = cd_material_p;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
