CREATE OR REPLACE FUNCTION get_drs_category_info ( ie_categoria_p text, ie_return_info_p text) RETURNS varchar AS $body$
DECLARE


ds_return_w 	varchar(50);


BEGIN

if (ie_return_info_p IS NOT NULL AND ie_return_info_p::text <> '')	then

	if (ie_return_info_p = 'C')	then

		CASE
