			WHEN(ie_categoria_p = 'M') THEN ds_return_w := 'thirdCell:#ffcd05';
			WHEN  ((ie_categoria_p = 'S') OR (ie_categoria_p = 'H')) THEN ds_return_w := 'thirdCell:#fa5a48';
			ELSE  ds_return_w := null;
		END CASE;

	elsif (ie_return_info_p = 'T')	then

		CASE
			WHEN  ((ie_categoria_p = 'L') OR (ie_categoria_p = 'N')) THEN ds_return_w := obter_desc_expressao_idioma(327348,null,wheb_usuario_pck.get_nr_seq_idioma);
			WHEN(ie_categoria_p = 'M') THEN ds_return_w := obter_desc_expressao_idioma(293044,null,wheb_usuario_pck.get_nr_seq_idioma);
			WHEN  ((ie_categoria_p = 'S') OR (ie_categoria_p = 'H')) THEN ds_return_w := obter_desc_expressao_idioma(327347,null,wheb_usuario_pck.get_nr_seq_idioma);
			ELSE  ds_return_w := null;
		END CASE;
	end if;

end if;

return ds_return_w;

end;
