CREATE OR REPLACE FUNCTION get_ds_item_dash (nr_sequencia_p bigint, nr_item_p bigint) RETURNS varchar AS $body$
DECLARE

  ds_item_w varchar(255);

BEGIN
select coalesce(b.ds_datamodel, 'Datamodel ' || b.nr_sequencia) || '.' || c.ds_campo ds
INTO STRICT ds_item_w
  from dar_app_datamodels a,
       dar_tables_control b, 
       dar_tab_control_fields c
 where coalesce(a.nr_seq_ligacao,a.nr_seq_table_control) = b.nr_sequencia
   and a.nr_seq_table_control = c.nr_seq_table_control
   and a.nr_seq_app = nr_sequencia_p
   and c.nr_sequencia = nr_item_p;
 RETURN ds_item_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
