SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_dt_sessao_quimio ( nr_seq_paciente_setor_p bigint, nr_seq_paciente_p bigint, nr_ciclo_p bigint, nr_sequencia_p bigint, ie_opcao_p text, DT_BASE_P timestamp DEFAULT clock_timestamp()) RETURNS timestamp AS $body$
DECLARE


	
dt_retorno_w		timestamp;
dt_max_dt_real_w 	timestamp;
dt_max_dt_prevista_w 	timestamp;
dt_quimio_w timestamp;

  r RECORD;

BEGIN

if	(nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') then

        SELECT 	CASE WHEN ie_opcao_p='R' THEN  max(y.dt_real) WHEN ie_opcao_p='P' THEN  max(y.dt_prevista) END 
        into STRICT	dt_retorno_w
        FROM	paciente_atendimento y
        WHERE 	y.nr_seq_atendimento = nr_seq_paciente_p;

elsif	(nr_seq_paciente_setor_p IS NOT NULL AND nr_seq_paciente_setor_p::text <> '') then   
    
        for r in (  SELECT 	w.dt_real, w.dt_prevista
                FROM	paciente_atendimento w
                WHERE 	w.nr_seq_paciente = nr_seq_paciente_setor_p
                AND     	W.NR_CICLO 		=  nr_ciclo_p
                AND     	coalesce(W.DT_CANCELAMENTO::text, '') = ''
                order by 	1) loop
        
            --DBMS_OUTPUT.PUT_LINE(to_char(r.dt_real,'dd/mm/yyyy hh24:mi:ss'));
            --DBMS_OUTPUT.PUT_LINE(to_char(r.dt_prevista,'dd/mm/yyyy hh24:mi:ss'));
            
            dt_max_dt_real_w := r.dt_real;
            dt_max_dt_prevista_w := r.dt_prevista;

            if ie_opcao_p = 'R' then
            
                if	r.dt_real > DT_BASE_P and
                coalesce(dt_retorno_w::text, '') = '' then
        
                dt_retorno_w := r.dt_real;

                end if;

            elsif ie_opcao_p = 'P' then
                
                if	r.dt_prevista > DT_BASE_P and
                coalesce(dt_retorno_w::text, '') = '' then
        
                dt_retorno_w := r.dt_prevista;

                end if;

            end if;
        end loop;

        if	coalesce(dt_retorno_w::text, '') = '' then
        
            if ie_opcao_p = 'R' then
            
                dt_retorno_w := dt_max_dt_real_w;

            elsif ie_opcao_p = 'P' then
                
                dt_retorno_w := dt_max_dt_prevista_w;

            end if;
            
        end if;

end if;

return dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
