CREATE OR REPLACE FUNCTION get_due_age ( dt_vencimento_p timestamp, dt_parametro_p timestamp ) RETURNS bigint AS $body$
DECLARE


/*Return
0 = Due between 0 and 30 days
30 = Due between 31 and 60 days
60 = Due between 61 and 90 dasy
90 = Due between 91 and 120 days
*/
nr_return_w	bigint;


BEGIN

if (dt_vencimento_p >= dt_parametro_p + 120) then
	nr_return_w := 120;
elsif (dt_vencimento_p >= dt_parametro_p + 90) then
	nr_return_w := 90;
elsif (dt_vencimento_p >= dt_parametro_p + 60) then
	nr_return_w := 60;
elsif (dt_vencimento_p >= dt_parametro_p + 30) then
	nr_return_w := 30;
else
	nr_return_w := 0;
end if;

return nr_return_w;

end;
$body$
