CREATE OR REPLACE FUNCTION get_ehro_chart_mode ( nm_usuario_p text, cd_code_group_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


chart_mode_w  varchar(255);


BEGIN

select coalesce(max(ie_agrupado),'N')
into STRICT chart_mode_w
from pepo_grafico_usuario
where nm_usuario = nm_usuario_p
and	cd_code_group_p = 'SV';

if (ie_opcao_p = 'D') then

	if (chart_mode_w = 'S') then
		chart_mode_w := 'grouped';
	else
		chart_mode_w := 'individual';
	end if;

end if;

return chart_mode_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
