CREATE OR REPLACE FUNCTION get_epae_active_records_count (nr_cirurgia_p bigint, nr_atendimento_p bigint, cd_pessoa_fisica_p bigint, nr_seq_agenda_p bigint, nr_seq_lista_espera_p bigint, nr_seq_pepo_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


qt_registros_ativos_w		bigint := 0;
qt_count_w 			bigint := 0;
nr_cirurgia_w			bigint := nr_cirurgia_p;
nr_atendimento_w			bigint := nr_atendimento_p;
cd_pessoa_fisica_w		bigint := cd_pessoa_fisica_p;
nr_seq_agenda_w			bigint := nr_seq_agenda_p;
nr_seq_lista_espera_w		bigint := nr_seq_lista_espera_p;
nr_seq_pepo_w			bigint := nr_seq_pepo_p;


BEGIN

if (nr_seq_agenda_w > 0 or nr_seq_lista_espera_w > 0) then
	begin

	SELECT 	COUNT(*)
	into STRICT 	qt_count_w
	FROM	AVAL_PRE_ANESTESICA a
	WHERE (nr_seq_agenda = nr_seq_agenda_w OR nr_seq_lista_espera = nr_seq_lista_espera_w)
	AND	((nm_usuario = nm_usuario_p
	AND	coalesce(dt_liberacao::text, '') = '') OR (dt_liberacao IS NOT NULL AND dt_liberacao::text <> ''));

	if (qt_count_w = 0) then
		begin
		if (nr_seq_agenda_w > 0) then
			nr_seq_agenda_w := 0;
		else
			nr_seq_lista_espera_w := 0;
		end if;

		nr_atendimento_w := 0;
		nr_cirurgia_w := 0;
		end;
	end if;

	end;
end if;

if (cd_pessoa_fisica_p > 0 and nr_atendimento_w = 0 and nr_cirurgia_w = 0 and nr_seq_agenda_w = 0 and nr_seq_lista_espera_w = 0 and nr_seq_pepo_w = 0) then
	select count(1)
	into STRICT qt_registros_ativos_w
	from AVAL_PRE_ANESTESICA
	where cd_pessoa_fisica = cd_pessoa_fisica_p
	and ((dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') or (nm_usuario = nm_usuario_p))
	and coalesce(ie_situacao, 'A') <> 'I';
elsif (nr_atendimento_w > 0 and nr_cirurgia_w = 0 and nr_seq_agenda_w = 0 and nr_seq_lista_espera_w = 0 and nr_seq_pepo_w = 0) then
	select count(1)
	into STRICT qt_registros_ativos_w
	from AVAL_PRE_ANESTESICA
	where nr_atendimento = nr_atendimento_w
	and ((dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') or (nm_usuario = nm_usuario_p))
	and coalesce(ie_situacao, 'A') <> 'I';
elsif (nr_cirurgia_w > 0 and nr_seq_agenda_w = 0 and nr_seq_lista_espera_w = 0 and nr_seq_pepo_w = 0) then
	select count(1)
	into STRICT qt_registros_ativos_w
	from AVAL_PRE_ANESTESICA
	where nr_cirurgia = nr_cirurgia_w
	and ((dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') or (nm_usuario = nm_usuario_p))
	and coalesce(ie_situacao, 'A') <> 'I';
elsif (nr_seq_pepo_w > 0 and nr_seq_agenda_w = 0 and nr_seq_lista_espera_w = 0) then
	select count(1)
	into STRICT qt_registros_ativos_w
	from AVAL_PRE_ANESTESICA
	where nr_seq_pepo = nr_seq_pepo_w
	and ((dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') or (nm_usuario = nm_usuario_p))
	and coalesce(ie_situacao, 'A') <> 'I';
elsif (nr_seq_agenda_w > 0 and nr_seq_lista_espera_w = 0) then
	select count(1)
	into STRICT qt_registros_ativos_w
	from AVAL_PRE_ANESTESICA
	where nr_seq_agenda = nr_seq_agenda_w
	and ((dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') or (nm_usuario = nm_usuario_p))
	and coalesce(ie_situacao, 'A') <> 'I';
else
	select count(1)
	into STRICT qt_registros_ativos_w
	from AVAL_PRE_ANESTESICA
	where nr_seq_lista_espera = nr_seq_lista_espera_w
	and ((dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') or (nm_usuario = nm_usuario_p))
	and coalesce(ie_situacao, 'A') <> 'I';
end if;

return	qt_registros_ativos_w;

end;
$body$
