CREATE OR REPLACE FUNCTION get_episode_by_surgery (nr_atendimento_p bigint, nr_cirurgia_p bigint, nr_seq_pepo_p bigint) RETURNS bigint AS $body$
DECLARE


nr_cirurgia_princ_w	bigint	:= 0;
nr_atendimento_w	bigint 	:= 0;


BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then
	nr_atendimento_w := nr_atendimento_p;
elsif (coalesce(nr_cirurgia_p,0) > 0) then
	select 	coalesce(max(nr_atendimento),0)
	into STRICT 	nr_atendimento_w
	from 	cirurgia
	where 	nr_cirurgia = nr_cirurgia_p;
end if;

if (nr_atendimento_w = 0) then
	if (coalesce(nr_seq_pepo_p,0) > 0) then
		select 	coalesce(max(nr_cirurgia_principal),0)
		into STRICT 	nr_cirurgia_princ_w
		from	pepo_cirurgia
		where	nr_sequencia = nr_seq_pepo_p;

		if (nr_cirurgia_princ_w > 0) then
			select	coalesce(max(nr_atendimento),0)
			into STRICT 	nr_atendimento_w
			from	cirurgia
			where  	nr_cirurgia = nr_cirurgia_princ_w;
		end if;

		if (nr_atendimento_w = 0) then
			select 	coalesce(max(nr_atendimento),0)
			into STRICT 	nr_atendimento_w
			from	cirurgia
			where	nr_seq_pepo 		= nr_seq_pepo_p
			and		ie_status_cirurgia 	<> 3;
		end if;
	end if;
end if;

return	nr_atendimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
