CREATE OR REPLACE FUNCTION get_establishment_timezone (cd_estab bigint) RETURNS varchar AS $body$
DECLARE


TIME_ZONE_ID  establishment_locale.ds_timezone%type;
BASE_LINE_TIMEZONE varchar(100);

BEGIN
    select	TIMEZONE_UTILS.getBaseLineTimeZone()
    into STRICT	BASE_LINE_TIMEZONE
;

    IF (coalesce(BASE_LINE_TIMEZONE::text, '') = '') then
        select	pkg_i18n.get_estab_timezone
        into STRICT	TIME_ZONE_ID
;

    ELSE
        select	ds_timezone
        into STRICT	TIME_ZONE_ID
        from establishment_locale
        where	cd_estabelecimento = cd_estab;

    END IF;

    RETURN TIME_ZONE_ID;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
