SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_exam_names (nr_atendimento_p bigint, nr_seq_classif_p bigint) RETURNS varchar AS $body$
DECLARE


ds_exam_name_w		varchar(100) := '';
ds_exam_list_w	varchar(500) := '';
qt_lengt_w		smallint;

c02 CURSOR FOR

SELECT  obter_desc_proc_exame(a.nr_seq_proc_interno) ds_exam
from	cpoe_procedimento a, proc_interno b
where	a.ie_pre_cons_exam = 'S'
and	coalesce(a.dt_liberacao::text, '') = ''
and	a.nr_atendimento = nr_atendimento_p
and	a.nr_seq_proc_interno = b.nr_sequencia
and	b.nr_seq_classif = nr_seq_classif_p;


BEGIN
open c02;
loop
fetch	c02 into
	ds_exam_name_w;
EXIT WHEN NOT FOUND; /* apply on c02 */
begin
	qt_lengt_w := coalesce(length(ds_exam_list_w),0);
	if (qt_lengt_w < 500) then
		ds_exam_list_w	:= ds_exam_list_w || ',' || ds_exam_name_w;
	end if;
end;
end loop;

ds_exam_list_w := substr(ds_exam_list_w,2,length(ds_exam_list_w));

return ds_exam_list_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION get_exam_names (nr_atendimento_p bigint, nr_seq_classif_p bigint) FROM PUBLIC;

