CREATE OR REPLACE FUNCTION get_exam_physician_list ( nr_prescricao_p prescr_procedimento.nr_prescricao%TYPE, nr_sequencia_prescricao_p prescr_procedimento.nr_sequencia%TYPE ) RETURNS varchar AS $body$
DECLARE

    ds_physician_list_w        lista_central_medico.ds_lista%TYPE;
    nr_count_physician_lists   bigint;

BEGIN
    ds_physician_list_w := '';
    SELECT
        COUNT(1)
    INTO STRICT nr_count_physician_lists
    FROM
        lista_central_exame    lce
        INNER JOIN lista_central_medico   lcm ON lce.nr_seq_lista_medico = lcm.nr_sequencia
    WHERE
        lce.nr_prescricao = nr_prescricao_p
        AND lce.nr_sequencia_prescricao = nr_sequencia_prescricao_p;

    IF nr_count_physician_lists <= 1 THEN
        SELECT
            lcm.ds_lista
        INTO STRICT ds_physician_list_w
        FROM
            lista_central_exame    lce
            INNER JOIN lista_central_medico   lcm ON lce.nr_seq_lista_medico = lcm.nr_sequencia
        WHERE
            lce.nr_prescricao = nr_prescricao_p
            AND lce.nr_sequencia_prescricao = nr_sequencia_prescricao_p;

    ELSE
        ds_physician_list_w := obter_desc_expressao(1023527);
    END IF;

    RETURN ds_physician_list_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
