CREATE OR REPLACE FUNCTION get_exam_sort_order_phy_list ( nr_prescricao_p prescr_procedimento.nr_prescricao%TYPE, nr_sequencia_prescricao_p prescr_procedimento.nr_sequencia%TYPE, nr_physician_list_p lista_central_medico.nr_sequencia%TYPE ) RETURNS varchar AS $body$
DECLARE

    nr_sort_order_w bigint;

BEGIN
    SELECT
        MAX(lce.nr_ordem_apresentacao)
    INTO STRICT nr_sort_order_w
    FROM
        lista_central_exame    lce
        INNER JOIN lista_central_medico   lcm ON lce.nr_seq_lista_medico = lcm.nr_sequencia
    WHERE
        lce.nr_prescricao = nr_prescricao_p
        AND lce.nr_sequencia_prescricao = nr_sequencia_prescricao_p
        AND coalesce(nr_physician_list_p, lcm.nr_sequencia) = lcm.nr_sequencia;

    RETURN nr_sort_order_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
