CREATE OR REPLACE FUNCTION get_external_department_code (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_retorno_w	 bigint;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select max(nr_externo)
	into STRICT   nr_retorno_w
	from unidade_atendimento
	where nr_atendimento = nr_atendimento_p
	and ie_situacao = 'A';
end if;

return nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
