CREATE OR REPLACE FUNCTION get_food_amount_consumed ( nr_sequencia_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w		prescr_mat_alteracao.nr_sequencia%type;
ds_amount_w		adep_qualidade_refeicao.ds_qualidade%type := null;



BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '')	then

	select  max(nr_sequencia)
	into STRICT    nr_sequencia_w
	from    prescr_mat_alteracao
	where   nr_seq_servico = nr_sequencia_p
	and     nr_atendimento = nr_atendimento_p
	and     ie_alteracao = 3;


	select  max(obter_desc_qualidade_refeicao(nr_seq_qualidade))
	into STRICT 	ds_amount_w
	from    prescr_mat_alteracao
	where   nr_sequencia = nr_sequencia_w;

end if;


return	ds_amount_w;

end;
