CREATE OR REPLACE FUNCTION get_hemodialysis_qt_ktv ( cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE


nr_retorno_w HD_CONTROLE.QT_KTV%type;

BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
	select 	max(qt_ktv)
	into STRICT 	nr_retorno_w
	from (SELECT 	c.QT_KTV qt_ktv
			from	HD_CONTROLE c,
					HD_DIALISE d
			where 	c.nr_seq_dialise = d.nr_sequencia
			and   	d.cd_pessoa_fisica = cd_pessoa_fisica_p
			order by c.dt_atualizacao desc) alias2 LIMIT 1;
	end;
end if;
return	nr_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
