CREATE OR REPLACE FUNCTION get_icon_profile_rule (nr_seq_icon_p bigint, nr_seq_regra_p bigint, ie_type_p text) RETURNS varchar AS $body$
DECLARE

/*
O - Sequence Order
D - Description
A - Avaiable
*/
w_ds_retorno varchar(255) := null;

BEGIN
	if (ie_type_p = 'O') then
		select max(nr_seq_prioridade)
		  into STRICT w_ds_retorno
		  from regra_perm_panorama_icone
	     where cd_icone_panorama = nr_seq_icon_p
		   and nr_seq_regra = nr_seq_regra_p;
	elsif (ie_type_p = 'D') then
		select max(ds_icone_instituicao)
		  into STRICT w_ds_retorno
		  from regra_perm_panorama_icone
	     where cd_icone_panorama = nr_seq_icon_p
		   and nr_seq_regra = nr_seq_regra_p;
	elsif (ie_type_p = 'A') then
	    select CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
		  into STRICT w_ds_retorno
		  from regra_perm_panorama_icone 
	     where cd_icone_panorama = nr_seq_icon_p
		   and nr_seq_regra = nr_seq_regra_p;
	end if;
return w_ds_retorno;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
