CREATE OR REPLACE FUNCTION get_ie_tipo_obj_navegador (NR_SEQ_OBJETO_ATUAL_P bigint) RETURNS varchar AS $body$
DECLARE


IE_TIPO_OBJ_NAVEGADOR_W varchar(100);WITH RECURSIVE cte AS (



BEGIN

IF (NR_SEQ_OBJETO_ATUAL_P IS NOT NULL AND NR_SEQ_OBJETO_ATUAL_P::text <> '') THEN
	BEGIN

  SELECT  IE_TIPO_OBJ_NAVEGADOR
  INTO STRICT    IE_TIPO_OBJ_NAVEGADOR_W,ARRAY[ row_number() OVER (ORDER BY  NR_SEQ_APRES) ] as hierarchy
  FROM    OBJETO_SCHEMATIC A
   WHERE A.NR_SEQUENCIA = NR_SEQ_OBJETO_ATUAL_P
  AND     A.NR_SEQ_FUNCAO_SCHEMATIC = 333
  UNION ALL



BEGIN

IF (NR_SEQ_OBJETO_ATUAL_P IS NOT NULL AND NR_SEQ_OBJETO_ATUAL_P::text <> '') THEN
	BEGIN

  SELECT  IE_TIPO_OBJ_NAVEGADOR
  INTO STRICT    IE_TIPO_OBJ_NAVEGADOR_W, array_append(c.hierarchy, row_number() OVER (ORDER BY  NR_SEQ_APRES))  as hierarchy
  FROM    OBJETO_SCHEMATIC A
   JOIN cte c ON (c.NR_SEQUENCIA = A.NR_SEQ_OBJ_SUP)

) SELECT * FROM cte WHERE NR_SEQ_FUNCAO_SCHEMATIC = 333
  AND     IE_TIPO_OBJETO = 'MN'
  AND     IE_TIPO_OBJ_NAVEGADOR IN ('T','TG') ORDER BY hierarchy;
 LIMIT 1;

	END;
END IF;

RETURN	IE_TIPO_OBJ_NAVEGADOR_W;

END;
