CREATE OR REPLACE FUNCTION get_if_alternative_route ( nr_seq_mat_cpoe_p bigint) RETURNS varchar AS $body$
DECLARE


ds_return_w 	varchar(1) := 'N';


BEGIN

if (nr_seq_mat_cpoe_p IS NOT NULL AND nr_seq_mat_cpoe_p::text <> '') then
	
	select 	CASE WHEN count(1)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ds_return_w
	from	cpoe_alternative_route
	where	nr_seq_mat_cpoe = nr_seq_mat_cpoe_p;

end if;

return ds_return_w;

end;
