SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_if_encounter_still_pa ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_return_w		varchar(255) := 'N';
ie_outcome_w		varchar(1);
ie_hospitalized_w	varchar(1);


BEGIN

begin

	ie_outcome_w := obter_dados_desfecho_pa(nr_atendimento_p, 'DES');
	
	select	CASE WHEN coalesce(count(*),0)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_hospitalized_w
	from	atendimento_alta a, parametro_medico p
	where	a.nr_atendimento = nr_atendimento_p
	and	a.ie_desfecho = 'I'
	and     p.cd_estabelecimento = (SELECT cd_estabelecimento from atendimento_paciente where nr_atendimento = nr_atendimento_p)
	and 	((a.ie_tipo_orientacao <> 'P')
	or (coalesce(p.ie_liberar_desfecho,'N')  = 'N') 
	or  	((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') and (coalesce(a.dt_inativacao::text, '') = '')))
	and	(obter_atepacu_paciente(nr_atendimento, 'PI') IS NOT NULL AND (obter_atepacu_paciente(nr_atendimento, 'PI'))::text <> '');
			
	if (ie_outcome_w = 'N' or ie_hospitalized_w = 'N') then
		ds_return_w := 'S';
	end if;

exception
when others then
	ds_return_w := 'N';
end;

return ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
