SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_if_famphys (cd_pessoa_fisica_p text, nr_seq_tipo_medico_p bigint) RETURNS varchar AS $body$
DECLARE


ie_existe_medico_familia_w	varchar(10);
ie_medico_familia_w		varchar(10);


BEGIN
select   (CASE WHEN (max(tme.nr_sequencia) IS NOT NULL AND (max(tme.nr_sequencia))::text <> '') THEN 'S' ELSE 'N' END)
into STRICT    ie_medico_familia_w
from    tipo_medico_externo tme
where   tme.ie_medico_familia = 'S'
and     tme.ie_situacao = 'A'
and     tme.nr_sequencia = nr_seq_tipo_medico_p;

if (ie_medico_familia_w = 'S')	then
	select	(CASE WHEN (max(pme.nr_sequencia) IS NOT NULL AND (max(pme.nr_sequencia))::text <> '') THEN 'S' ELSE 'N' END)
	into STRICT	ie_existe_medico_familia_w
	from	pf_medico_externo pme,
		tipo_medico_externo tme,
		medico me
	where	pme.nr_seq_tipo_medico = tme.nr_sequencia
	and	tme.ie_medico_familia = 'S'
	and	tme.ie_situacao = 'A'
	and	pme.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	me.cd_pessoa_fisica = pme.cd_medico
	and	me.ie_situacao = 'A'
	and (coalesce(pme.dt_fim_vigencia::text, '') = '' or (trunc(pme.dt_fim_vigencia) > trunc(clock_timestamp())));
else
ie_existe_medico_familia_w	:= 'N';
end if;

return	ie_existe_medico_familia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
