CREATE OR REPLACE FUNCTION get_if_free_bed_comp ( nr_atendimento_de_p bigint, nr_atendimento_para_p bigint, cd_unidade_bas_dest_p text, cd_unidade_compl_dest_p text, cd_setor_atend_dest_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(1) := 'S';
qt_acompanhate_w	bigint;
nr_agrupamento_w	integer;
qt_livre_w 		integer;
qt_acomp_acom_w		bigint;


BEGIN

if (nr_atendimento_de_p IS NOT NULL AND nr_atendimento_de_p::text <> '') and (nr_atendimento_para_p IS NOT NULL AND nr_atendimento_para_p::text <> '') then
	--Obter quantidade de acompanhantes
	select	count(*)
	into STRICT	qt_acompanhate_w
	from	unidade_atendimento a
	where 	a.nr_atendimento_acomp	= nr_atendimento_de_p
	and 	a.ie_status_unidade	= 'M';

	select 	a.nr_agrupamento
	into STRICT	nr_agrupamento_w
	from	unidade_atendimento a
	where	a.cd_unidade_basica	= cd_unidade_bas_dest_p
	and 	a.cd_unidade_compl	= cd_unidade_compl_dest_p
	and	a.cd_setor_atendimento	= cd_setor_atend_dest_p;

	if (qt_acompanhate_w > 0) then
		--Verificar se há espaço para os acompanhantes no mesmo quarto (Leito e unidade basica iguais)
		select	 count(*)
		into STRICT	 qt_livre_w
		from 	 unidade_atendimento a
		where	 a.cd_setor_atendimento	= cd_setor_atend_dest_p
		and	 a.cd_unidade_basica 	 = cd_unidade_bas_dest_p
		and     (((a.cd_unidade_compl	 <> cd_unidade_compl_dest_p)
		and (a.ie_status_unidade in ('L'))
		and (obter_se_sem_acomodacao(cd_tipo_acomodacao) = 'N'))  
		or 	 ((obter_se_sem_acomodacao(cd_tipo_acomodacao) = 'N') 
		and (coalesce(a.ie_situacao, 'A') = 'A')
		and (a.ie_status_unidade = 'I'))
		or 	 (a.nr_atendimento_acomp =  nr_atendimento_para_p AND a.ie_status_unidade	= 'M'));

		if (qt_livre_w < qt_acompanhate_w) then
			ds_retorno_w := 'N';
		end if;

	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
