CREATE OR REPLACE FUNCTION get_if_is_order_type ( nr_seq_proc_interno_p bigint, nr_seq_order_type_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


si_exists_order_type_w	varchar(1) := 'S';
exist_rule_w			varchar(1);	
		

BEGIN

select 	coalesce(max('S'), 'N')
into STRICT	exist_rule_w
from	proc_order_type
where	nr_seq_proc_interno = nr_seq_proc_interno_p;

if (exist_rule_w = 'S') and (nr_seq_order_type_p IS NOT NULL AND nr_seq_order_type_p::text <> '') then
	
	select 	coalesce(max('S'), 'N')
	into STRICT	si_exists_order_type_w
	from	proc_order_type
	where	nr_seq_proc_interno = nr_seq_proc_interno_p
	and		nr_seq_order_type = nr_seq_order_type_p
	and		cd_estabelecimento = coalesce(cd_estabelecimento_p, obter_estabelecimento_ativo);
	
end if;

return si_exists_order_type_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
