CREATE OR REPLACE FUNCTION get_if_itens_copia ( cd_empresa_p bigint, cd_estabelecimento_p bigint, nr_sequencia_p bigint ) RETURNS bigint AS $body$
DECLARE

    retorno_w bigint := 0;

BEGIN

    SELECT
        COUNT(1) AS qtd
    INTO STRICT retorno_w
    FROM
        (
            SELECT DISTINCT
                a.nr_sequencia AS nr_seq_grupo
            FROM pe_prescricao e, pe_item_tipo_item c, pe_tipo_item a, pe_item_examinar b
LEFT OUTER JOIN pe_sae_mod_item d ON (b.nr_sequencia = d.nr_seq_item)
WHERE ( ( a.cd_empresa = cd_empresa_p )
                  OR ( coalesce(a.cd_empresa::text, '') = '' ) ) AND a.nr_sequencia = c.nr_seq_tipo_item AND b.nr_sequencia = c.nr_seq_item  AND coalesce(a.cd_estabelecimento, coalesce(cd_estabelecimento_p, 0)) = coalesce(cd_estabelecimento_p, 0) AND coalesce(b.cd_estabelecimento, coalesce(cd_estabelecimento_p, 0)) = coalesce(cd_estabelecimento_p, 0) AND ( ( d.cd_setor_pac = e.cd_setor_atendimento )
                      OR ( coalesce(d.cd_setor_pac::text, '') = '' ) ) AND a.ie_situacao = 'A' AND e.nr_sequencia = nr_sequencia_p AND a.nr_sequencia NOT IN (
                    SELECT DISTINCT
                        a.nr_sequencia
                    FROM pe_prescricao e, pe_item_tipo_item c, pe_tipo_item a, pe_item_examinar b
LEFT OUTER JOIN pe_sae_mod_item d ON (b.nr_sequencia = d.nr_seq_item)
WHERE ( ( a.cd_empresa = cd_empresa_p )
                          OR ( coalesce(a.cd_empresa::text, '') = '' ) ) AND a.nr_sequencia = c.nr_seq_tipo_item AND b.nr_sequencia = c.nr_seq_item  AND ( ( coalesce(e.nr_seq_modelo, 0) = 0 )
                              OR ( d.nr_seq_modelo = e.nr_seq_modelo ) ) AND coalesce(a.cd_estabelecimento, coalesce(cd_estabelecimento_p, 0)) = coalesce(cd_estabelecimento_p, 0) AND coalesce(b.cd_estabelecimento, coalesce(cd_estabelecimento_p, 0)) = coalesce(cd_estabelecimento_p, 0) AND ( ( d.cd_setor_pac = e.cd_setor_atendimento )
                              OR ( coalesce(d.cd_setor_pac::text, '') = '' ) ) AND a.ie_situacao = 'A' AND e.nr_sequencia = nr_sequencia_p
                 ) AND a.nr_sequencia IN (
                    SELECT DISTINCT
                        c.nr_seq_tipo_item
                    FROM
                        pe_prescr_item_result   a,
                        pe_item_resultado       b,
                        pe_item_tipo_item       c
                    WHERE
                        a.nr_seq_prescr = nr_sequencia_p
                        AND a.nr_seq_result = b.nr_sequencia
                        AND c.nr_seq_item = b.nr_seq_item
                )
         ) alias35;

    RETURN retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
