CREATE OR REPLACE FUNCTION get_if_patient_allergic ( cd_pessoa_fisica_p text, cd_material_p bigint, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE

retorno_w	varchar(4000) := 'N';

ie_alergico_w	varchar(1) := 'N';
ds_mensagem_w	varchar(4000) := null;
ie_severidade_w	paciente_alergia.ie_intensidade%type;
nr_seq_reacao_w	paciente_alergia.nr_seq_reacao%type;
nr_seq_ficha_tecnica_w	paciente_alergia.nr_seq_ficha_tecnica%type;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '' AND cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	SELECT * FROM cpoe_verifica_se_pac_alergico(cd_pessoa_fisica_p, cd_material_p, ie_alergico_w, ds_mensagem_w, ie_severidade_w, nr_seq_reacao_w, 'N', nr_seq_ficha_tecnica_w) INTO STRICT ie_alergico_w, ds_mensagem_w, ie_severidade_w, nr_seq_reacao_w, nr_seq_ficha_tecnica_w;
end if;

if (ie_tipo_retorno_p = 'A') then
	retorno_w := ie_alergico_w;
else
	retorno_w := ds_mensagem_w;
end if;

return	retorno_w;

end;
$body$
