CREATE OR REPLACE FUNCTION get_if_professional_certified ( nm_usuario_p text, si_special_license_p text, dt_reference_p timestamp) RETURNS varchar AS $body$
DECLARE

		
ds_return_w			varchar(1) := 'N';
cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;


BEGIN

cd_pessoa_fisica_w	:= obter_pessoa_fisica_usuario(nm_usuario_p, 'C');

if (si_special_license_p IS NOT NULL AND si_special_license_p::text <> '') and (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then

	select	CASE WHEN count(1)=0 THEN  'N'  ELSE 'Y' END
	into STRICT	ds_return_w
	from	pessoa_documentacao a
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_w
	and		a.si_special_license = si_special_license_p
	and		a.ie_situacao = 'A'
	and (a.dt_validade > dt_reference_p or coalesce(a.dt_validade::text, '') = '');
	
end if;

return ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
